:root {
  --luma-accordion-icon-rotation: 90deg;
  --luma-accordion-transition-duration: 150ms;
  --luma-accordion-item-radius: 8px;
}

/* Light mode */
:root {
  --luma-accordion-item-border: oklch(0.92 0.008 265);
  --luma-accordion-trigger-background-hover: oklch(0.97 0.005 265);
}

/* Dark mode */
.dark {
  --luma-accordion-item-border: oklch(0.35 0.01 265);
  --luma-accordion-trigger-background-hover: oklch(0.22 0.01 265);
}

/* FAQ section with specific styling */
.faq-section {
  --luma-accordion-item-radius: 0;
  --luma-accordion-title-font-weight: 600;
}

/* Style based on state */
luma-accordion-item[data-state='open'] {
  --luma-accordion-item-border: oklch(0.54 0.1 230);
}

/* Style based on variant */
luma-accordion-item[data-variant='filled'] {
  --luma-accordion-filled-background: oklch(0.97 0.005 265);
}

/* In your global styles.css */
:root {
  --luma-button-radius: 4px; /* More sharp */
}

/* Light mode - subtle rounding */
:root {
  --luma-button-radius: 8px;
}

/* Dark mode - more pronounced rounding */
.dark {
  --luma-button-radius: 16px;
}

/* Only buttons in the header */
.header {
  --luma-button-radius: 4px;
}

/* Only buttons in cards */
.card {
  --luma-button-radius: 12px;
}

/* In your global styles.css */
:root {
  --luma-card-shadow-background: #ffffff;
  --luma-card-shadow-shadow: 0 4px 12px rgba(0, 0, 0, 0.05);
  --luma-card-padding: 24px;
}

/* Light mode */
:root {
  --luma-card-shadow-background: #fafafa;
}

/* Dark mode */
.dark {
  --luma-card-shadow-background: oklch(0.18 0.005 265);
  --luma-card-nested-background: oklch(0.2 0.005 265);
}

/* Cards in sidebar - more compact */
.sidebar {
  --luma-card-padding: 16px;
}

/* Cards in hero section - more prominent shadow */
.hero {
  --luma-card-shadow-shadow: 0 12px 32px oklch(0.22 0.01 265 / 0.1);
}

@Component({
  template: `
    <button lumaButton (click)="openUserModal()">View User</button>

    <luma-modal [lmOpen]="isOpen()" (lmOpenChange)="isOpen.set($event)">
      <luma-modal-overlay>
        <luma-modal-container>
          <luma-modal-close />
          <div lumaModalHeader>
            <h2 lumaModalTitle>User Details</h2>
          </div>
          <div lumaModalContent>
            @if (loading()) {
              <div class="flex justify-center py-8">
                <span class="lm-text-secondary">Loading...</span>
              </div>
            } @else if (user()) {
              <div class="space-y-2">
                <p><strong>Name:</strong> {{ user()?.name }}</p>
                <p><strong>Email:</strong> {{ user()?.email }}</p>
              </div>
            }
          </div>
        </luma-modal-container>
      </luma-modal-overlay>
    </luma-modal>
  `,
})
export class UserModalComponent {
  private userService = inject(UserService);

  isOpen = signal(false);
  loading = signal(false);
  user = signal<User | null>(null);

  openUserModal() {
    this.isOpen.set(true);
    this.loading.set(true);

    this.userService
      .getUser(123)
      .pipe(
        tap((user) => this.user.set(user)),
        finalize(() => this.loading.set(false)),
      )
      .subscribe();
  }
}

@Component({
  template: `
    <luma-modal [lmOpen]="isOpen()" (lmOpenChange)="handleOpenChange($event)">
      <luma-modal-overlay>
        <luma-modal-container>
          <div lumaModalHeader>
            <h2 lumaModalTitle>Edit Profile</h2>
            <luma-modal-close />
          </div>
          <div lumaModalContent>
            <input [(ngModel)]="formData().name" placeholder="Name" />
            <input [(ngModel)]="formData().email" placeholder="Email" />
          </div>
          <div lumaModalFooter>
            <button lumaButton lmVariant="ghost" (click)="cancel()">
              Cancel
            </button>
            <button lumaButton lmVariant="primary" (click)="save()">
              Save
            </button>
          </div>
        </luma-modal-container>
      </luma-modal-overlay>
    </luma-modal>
  `,
})
export class EditProfileModalComponent {
  isOpen = signal(false);
  formData = signal({ name: '', email: '' });

  handleOpenChange(open: boolean) {
    if (!open) {
      // Modal closing - execute cleanup
      this.formData.set({ name: '', email: '' });
      this.analytics.track('profile_modal_closed');
    }
    this.isOpen.set(open);
  }

  save() {
    this.profileService.update(this.formData());
    this.isOpen.set(false);
  }

  cancel() {
    this.isOpen.set(false);
  }
}

@Component({
  template: `
    <luma-modal
      [lmOpen]="isOpen()"
      (lmOpenChange)="handleOpenChange($event)"
      [lmCloseOnOverlay]="!hasChanges()"
      [lmCloseOnEscape]="!hasChanges()"
    >
      <luma-modal-overlay>
        <luma-modal-container>
          <div lumaModalHeader>
            <h2 lumaModalTitle>Document</h2>
            <luma-modal-close />
          </div>
          <div lumaModalContent>
            <textarea
              [ngModel]="content()"
              (ngModelChange)="onContentChange($event)"
            ></textarea>
          </div>
          <div lumaModalFooter>
            <button
              lumaButton
              lmVariant="ghost"
              (click)="handleOpenChange(false)"
            >
              Close
            </button>
            <button
              lumaButton
              lmVariant="primary"
              [disabled]="!hasChanges()"
              (click)="save()"
            >
              Save
            </button>
          </div>
        </luma-modal-container>
      </luma-modal-overlay>
    </luma-modal>

    <!-- Confirmation modal -->
    <luma-modal
      [lmOpen]="showConfirm()"
      (lmOpenChange)="showConfirm.set($event)"
    >
      <luma-modal-overlay>
        <luma-modal-container>
          <div lumaModalContent>
            <p>You have unsaved changes. Discard and close?</p>
          </div>
          <div lumaModalFooter>
            <button
              lumaButton
              lmVariant="ghost"
              (click)="showConfirm.set(false)"
            >
              Keep editing
            </button>
            <button lumaButton lmVariant="danger" (click)="discardAndClose()">
              Discard changes
            </button>
          </div>
        </luma-modal-container>
      </luma-modal-overlay>
    </luma-modal>
  `,
})
export class DocumentModalComponent {
  isOpen = signal(false);
  showConfirm = signal(false);
  content = signal('');
  originalContent = signal('');

  hasChanges = computed(() => this.content() !== this.originalContent());

  onContentChange(value: string) {
    this.content.set(value);
  }

  handleOpenChange(open: boolean) {
    if (!open && this.hasChanges()) {
      this.showConfirm.set(true);
      return;
    }
    this.isOpen.set(open);
  }

  discardAndClose() {
    this.showConfirm.set(false);
    this.content.set(this.originalContent());
    this.isOpen.set(false);
  }

  save() {
    this.originalContent.set(this.content());
    this.isOpen.set(false);
  }
}

